import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the header to appear using role and text (alternative strategy)
    await page.waitForSelector('header.header', { timeout: 4000 });

    // Wait for the username element using xpath (alternative selector)
    const usernameXpath = "//header//span[contains(@class, 'username') and normalize-space(text())='jun']";
    await page.waitForSelector(`xpath=${usernameXpath}`, { timeout: 4000 });

    // Wait for the avatar using alternative selector: select the img inside user-menu by alt attribute
    const avatarSelector = "header .user-menu .user-avatar img[alt='User Avatar']";
    await page.waitForSelector(avatarSelector, { timeout: 4000 });

    // Take screenshot of the user menu section for proof
    const userMenuHandle = await page.$('header .user-menu');
    if (userMenuHandle) {
      await userMenuHandle.screenshot({ path: path.join(screenshotsDir, 'step_2_user_menu.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_user_menu.png'));
    }

    // Extract username text using the found element (xpath)
    const usernameElement = await page.$(`xpath=${usernameXpath}`);
    const username = usernameElement ? (await usernameElement.textContent()).trim() : null;

    // Extract avatar src
    const avatarElement = await page.$(avatarSelector);
    const avatarSrc = avatarElement ? await avatarElement.getAttribute('src') : null;

    // Validate the username and avatar
    const usernameMatches = username === 'jun';
    const avatarIsPresent = avatarSrc && avatarSrc.startsWith('data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNDAiIGhlaWdodD0iNDAi');

    // Take a final screenshot of the full header for context
    const headerHandle = await page.$('header.header');
    if (headerHandle) {
      await headerHandle.screenshot({ path: path.join(screenshotsDir, 'step_3_header.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_header.png'));
    }

    return {
      success: usernameMatches && avatarIsPresent,
      action: "Verified that the user profile in the header displays the username 'jun' and the correct avatar.",
      extractedData: {
        username,
        avatarSrc,
        usernameMatches,
        avatarIsPresent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
