import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the header user profile area to be visible
    await page.waitForSelector('.header .user-menu', { timeout: 4000 });

    // Wait for username and avatar to be present
    await page.waitForSelector('.header .user-menu .username', { timeout: 4000 });
    await page.waitForSelector('.header .user-menu .user-avatar img', { timeout: 4000 });

    // Extract username text
    const username = await page.$eval('.header .user-menu .username', el => el.textContent.trim());

    // Extract avatar src
    const avatarSrc = await page.$eval('.header .user-menu .user-avatar img', el => el.getAttribute('src'));

    // Take screenshot of the header area
    const headerElement = await page.$('.header');
    if (headerElement) {
      await headerElement.screenshot({ path: path.join(screenshotsDir, 'step_2_header_profile.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_header_profile.png'));
    }

    // Check if username is 'jun' and avatar is present
    const usernameIsJun = username === 'jun';
    const avatarIsPresent = !!avatarSrc && avatarSrc.startsWith('data:image/svg+xml;base64,');

    // Compose extracted data
    const extractedData = {
      username,
      avatarSrc,
      usernameIsJun,
      avatarIsPresent
    };

    // Final assertion for success
    const success = usernameIsJun && avatarIsPresent;

    return {
      success,
      action: success
        ? "Verified that the user profile in the header displays username 'jun' and an avatar image"
        : "User profile display verification failed",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

