import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the header and user menu to appear
    await page.waitForSelector('header.header .user-menu', { timeout: 5000 });

    // Wait for the avatar image inside .user-avatar
    const avatarSelector = 'header.header .user-menu .user-avatar img';
    await page.waitForSelector(avatarSelector, { timeout: 3000 });

    // Wait for the username span
    const usernameSelector = 'header.header .user-menu .username';
    await page.waitForSelector(usernameSelector, { timeout: 3000 });

    // Extract the username text
    const username = await page.$eval(usernameSelector, el => el.textContent.trim());

    // Extract avatar src
    const avatarSrc = await page.$eval(avatarSelector, el => el.getAttribute('src'));

    // Take screenshot of the header area
    const headerElement = await page.$('header.header');
    if (headerElement) {
      await headerElement.screenshot({ path: path.join(screenshotsDir, 'step_2_header_user_profile.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_header_user_profile.png'));
    }

    // Verification logic
    const usernameIsJun = username === 'jun';
    const avatarIsPresent = !!avatarSrc && avatarSrc.startsWith('data:image/svg+xml;base64,');
    const avatarIsJun =
      avatarSrc &&
      avatarSrc.includes('IzYzNjZGM') // partial base64 for #6366F1 (jun's avatar color, from HTML)
      ;

    return {
      success: usernameIsJun && avatarIsPresent && avatarIsJun,
      action: "Verified that the user profile in the header displays username 'jun' and the correct avatar.",
      extractedData: {
        username,
        avatarSrc,
        usernameIsJun,
        avatarIsPresent,
        avatarIsJun
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    const screenshots = [];

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the header to be present
    await page.waitForSelector('header.header', { timeout: 5000 });

    // Use XPath to locate the username span and avatar in the user-menu area
    // This is a different strategy: we use XPath and traverse from known nav links
    const [usernameEl] = await page.$x("//nav[contains(@class,'nav-menu')]//div[contains(@class,'user-menu')]//span[contains(@class,'username')]");
    const [avatarImg] = await page.$x("//nav[contains(@class,'nav-menu')]//div[contains(@class,'user-avatar')]/img");

    // Wait for elements to be attached
    if (!usernameEl || !avatarImg) {
      throw new Error("User profile elements not found in header");
    }

    // Extract the username text
    const username = (await usernameEl.textContent())?.trim();

    // Extract the avatar src attribute
    const avatarSrc = await avatarImg.getAttribute('src');

    // Take a screenshot focusing on the user menu area (bounding box)
    const userMenu = await page.$("nav.nav-menu .user-menu");
    if (userMenu) {
      const box = await userMenu.boundingBox();
      if (box) {
        await page.screenshot({
          path: path.join(screenshotsDir, 'step_2_user_menu_area.png'),
          clip: {
            x: box.x,
            y: box.y,
            width: box.width,
            height: box.height
          }
        });
        screenshots.push(path.join(screenshotsDir, 'step_2_user_menu_area.png'));
      }
    }

    // Verification
    const usernameOk = username === 'jun';
    // Known avatar base64 prefix for jun
    const expectedAvatarPrefix = 'data:image/svg+xml;base64,';
    const avatarOk = typeof avatarSrc === 'string' && avatarSrc.startsWith(expectedAvatarPrefix);

    return {
      success: usernameOk && avatarOk,
      action: "Verified that the user profile header displays username 'jun' and the expected avatar image using XPath selectors.",
      extractedData: {
        username,
        avatarSrc,
        usernameOk,
        avatarOk
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the header to appear using role and text strategies
    await page.waitForSelector('header.header', { timeout: 5000 });

    // Wait for the username using a more robust selector (span.username with exact text)
    const usernameHandle = await page.waitForSelector('//header//span[contains(@class, "username") and normalize-space(text())="jun"]', { timeout: 4000 });

    // Wait for the avatar image using XPath (img inside .user-avatar within header)
    const avatarHandle = await page.waitForSelector('//header//div[contains(@class,"user-avatar")]/img', { timeout: 4000 });

    // Take screenshot after waiting for user info
    const userInfoShot = path.join(screenshotsDir, 'step_2_profile_header.png');
    await page.screenshot({ path: userInfoShot });
    screenshots.push(userInfoShot);

    // Extract username text and avatar src
    const username = await page.evaluate(el => el.textContent.trim(), usernameHandle);
    const avatarSrc = await page.evaluate(el => el.getAttribute('src'), avatarHandle);

    // Check if avatar is a data-url (for mock SVG)
    const isAvatarPresent = typeof avatarSrc === 'string' && avatarSrc.startsWith('data:image/svg+xml;base64,');

    // Compose extractedData
    const extractedData = {
      username,
      avatarSrc,
      avatarIsDataSVG: isAvatarPresent
    };

    // Final assertion logic
    const usernameCorrect = username === 'jun';
    const avatarCorrect = isAvatarPresent;

    if (!usernameCorrect || !avatarCorrect) {
      return {
        success: false,
        action: "User profile header did not display the correct username or avatar",
        extractedData,
        screenshots,
        error: (!usernameCorrect ? "Username not found or incorrect. " : "") + (!avatarCorrect ? "Avatar not found or not a data SVG." : "")
      };
    }

    return {
      success: true,
      action: "Verified that the user profile displays 'jun' as username and the avatar in the header.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
