import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the header user profile area to be visible
    await page.waitForSelector('.user-menu', { timeout: 5000 });

    // Wait for the username element
    const usernameSelector = '.user-menu .username';
    await page.waitForSelector(usernameSelector, { timeout: 3000 });

    // Wait for the avatar image
    const avatarSelector = '.user-menu .user-avatar img';
    await page.waitForSelector(avatarSelector, { timeout: 3000 });

    // Extract username text
    const username = await page.$eval(usernameSelector, el => el.textContent.trim());

    // Extract avatar src
    const avatarSrc = await page.$eval(avatarSelector, el => el.getAttribute('src'));

    // Take screenshot of the header area
    const headerElement = await page.$('header.header');
    if (headerElement) {
      await headerElement.screenshot({ path: path.join(screenshotsDir, 'step_2_header_profile.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_header_profile.png'));
    }

    // Verification logic
    const usernameCorrect = username === 'jun';
    const avatarIsPresent = !!avatarSrc && avatarSrc.startsWith('data:image/svg+xml;base64,');
    const avatarIsJun = avatarSrc && avatarSrc.includes('IzYzNjZGMS'); // This is the color code for jun's avatar in the mock data

    return {
      success: usernameCorrect && avatarIsPresent && avatarIsJun,
      action: "Verified that the user profile in the header displays username 'jun' and the correct avatar.",
      extractedData: {
        username,
        avatarSrc,
        usernameCorrect,
        avatarIsPresent,
        avatarIsJun
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

