    const usernameXPath = "//header//span[contains(@class,'username') and normalize-space(text())='jun']";
    await page.waitForSelector(`xpath=${usernameXPath}`, { timeout: 4000 });

    // Use another selector strategy for the avatar: target the <img> inside .user-avatar under .user-menu
    const avatarSelector = ".user-menu .user-avatar img";
    await page.waitForSelector(avatarSelector, { timeout: 4000 });

    // Extract username text
    const usernameEl = await page.$(`xpath=${usernameXPath}`);
    const usernameText = usernameEl ? (await usernameEl.textContent()).trim() : null;

    // Extract avatar src
    const avatarImgEl = await page.$(avatarSelector);
    const avatarSrc = avatarImgEl ? await avatarImgEl.getAttribute('src') : null;
    // Optionally, get alt attribute
    const avatarAlt = avatarImgEl ? await avatarImgEl.getAttribute('alt') : null;

    // Take screenshot of just the user-menu for clarity
    const userMenuEl = await page.$('.nav-menu .user-menu');
    if (userMenuEl) {
      await userMenuEl.screenshot({ path: path.join(screenshotsDir, 'step_2_user_menu.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_user_menu.png'));
    }

    // Check that username is 'jun' and avatar src is a data URL (as expected)
    const avatarIsDataUrl = avatarSrc && avatarSrc.startsWith('data:image/svg+xml;base64,');

    return {
      success: usernameText === 'jun' && !!avatarIsDataUrl,
      action: "Verified that the user profile displays the username 'jun' and an avatar image in the header.",
      extractedData: {
        username: usernameText,
        avatarSrc,
        avatarAlt
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot of the landing state
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // --- HEADER USER PROFILE CHECK ---

    // Wait for the header and user-menu to be visible
    await page.waitForSelector('header.header');
    await page.waitForSelector('.user-menu', { state: 'visible', timeout: 5000 });

    // Use alternative selector strategies for robustness

    // 1. Find the username by searching for the span inside .user-info
    const usernameSelector = '.user-menu .user-info .username';
    await page.waitForSelector(usernameSelector, { timeout: 3000 });

    // 2. Find the avatar image using XPath (alternative to .user-avatar)
    const avatarImgXPath = '//header//div[contains(@class,"user-avatar")]/img';
    const [avatarImgElement] = await page.$x(avatarImgXPath);
    if (!avatarImgElement) throw new Error('User avatar image not found in header');

    // 3. Extract username text content
    const usernameHandle = await page.$(usernameSelector);
    if (!usernameHandle) throw new Error('Username element not found in header');
    const usernameText = (await usernameHandle.innerText()).trim();

    // 4. Extract avatar src attribute
    const avatarSrc = await avatarImgElement.getAttribute('src');

    // 5. Take screenshot focusing on header
    const header = await page.$('header.header');
    let headerScreenshotPath = path.join(screenshotsDir, 'step_2_header_user_profile.png');
    if (header) {
      await header.screenshot({ path: headerScreenshotPath });
      screenshots.push(headerScreenshotPath);
    }

    // 6. Validate username and avatar src
    const expectedUsername = 'jun';
    // Use the base64 beginning as key check for the avatar (from data.js)
    const expectedAvatarPrefix = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNDAiIGhlaWdodD0iNDAiIHZpZXdCb3g9IjAgMCA0MCA0MCIgZmlsbD0ibm9uZSI'; 

    const usernameMatch = usernameText === expectedUsername;
    const avatarMatch = avatarSrc && avatarSrc.startsWith(expectedAvatarPrefix);

    // Prepare extracted data
    const extractedData = {
      username: usernameText,
      usernameMatch,
      avatarSrc,
      avatarMatch
    };

    // Both must match for success
    if (!usernameMatch) throw new Error(`Username in header is "${usernameText}", expected "${expectedUsername}"`);
    if (!avatarMatch) throw new Error(`User avatar src does not match expected base64 prefix`);

    return {
      success: true,
      action: "Verified that the user profile in header shows username 'jun' and the correct avatar",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
