import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible and click to open the dropdown menu
    await page.waitForSelector('.user-avatar img');
    await page.click('.user-avatar img');

    // Take a screenshot after clicking the avatar
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Verify if the dropdown menu is opened by checking the visibility of a dropdown item
    const dropdownVisible = await page.isVisible('.user-dropdown');
    if (!dropdownVisible) {
      throw new Error('Dropdown menu did not open.');
    }

    // Extract the displayed username for verification
    const username = await page.textContent('.username');

    return {
      success: true,
      action: "Opened user dropdown menu successfully",
      extractedData: { username },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
