import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible and click to open the dropdown menu
    await page.waitForSelector('.user-avatar', { visible: true });
    await page.click('.user-avatar');

    // Take screenshot after clicking the user avatar
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Verify the dropdown menu is visible
    const isDropdownVisible = await page.isVisible('.user-dropdown');

    return {
      success: true,
      action: "Opened user dropdown menu",
      extractedData: { dropdownVisible: isDropdownVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

