import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible and click to open the dropdown menu
    await page.waitForSelector('.user-avatar', { state: 'visible' });
    await page.click('.user-avatar');

    // Take screenshot after clicking the user avatar
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Verify that the dropdown menu is visible
    const dropdownVisible = await page.isVisible('.user-dropdown');

    return {
      success: true,
      action: "Opened the user dropdown menu",
      extractedData: { dropdownVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible and click to open the dropdown
    await page.waitForSelector('.user-avatar img', { timeout: 5000 });
    await page.click('.user-avatar img');

    // Wait for the dropdown menu to appear
    await page.waitForSelector('.user-dropdown', { state: 'visible', timeout: 5000 });

    // Take a screenshot of the dropdown menu opened
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    return {
      success: true,
      action: "Opened user dropdown menu",
      extractedData: {
        username: await page.textContent('.username'),
        dropdownItems: await page.$$eval('.dropdown-item', items => items.map(item => item.textContent))
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

