import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible and click to open the dropdown menu
    await page.waitForSelector('.user-avatar', { state: 'visible' });
    await page.click('.user-avatar');

    // Take screenshot after opening the dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Verify the dropdown items are visible
    const dropdownVisible = await page.isVisible('.user-dropdown');
    const extractedData = {
      dropdownOpened: dropdownVisible,
      items: await page.$$eval('.user-dropdown .dropdown-item', items => items.map(item => item.textContent))
    };

    return {
      success: true,
      action: "Opened user dropdown menu",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

