import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar element to be visible
    await page.waitForSelector('.user-avatar img');

    // Click on the user avatar to open the dropdown menu
    await page.click('.user-avatar img');

    // Wait for the dropdown menu to be visible
    await page.waitForSelector('.user-dropdown', { state: 'visible' });

    // Take screenshot after opening the dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Extracting the username from the dropdown for verification
    const username = await page.$eval('.username', el => el.textContent.trim());

    return {
      success: true,
      action: "Opened user dropdown menu",
      extractedData: { username },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
