import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify footer links presence
    const footerLinks = await page.$$('.footer-link');
    const expectedLinks = [
      { text: 'Home', href: 'index.html' },
      { text: 'My Events', href: 'dashboard.html' },
      { text: '+ New Event', href: 'create.html' },
      { text: 'Profile', href: '#' },
      { text: 'Settings', href: '#' },
      { text: 'Sign Out', href: '#' },
      { text: 'Create New Event', href: 'create.html' },
      { text: 'View All →', href: 'dashboard.html' }
    ];

    const extractedData = {};

    for (const expectedLink of expectedLinks) {
      const linkSelector = `a:has-text("${expectedLink.text}")`;
      await page.waitForSelector(linkSelector);
      const linkElement = await page.$(linkSelector);
      const href = await linkElement.evaluate(el => el.getAttribute('href'));

      extractedData[expectedLink.text] = {
        present: !!linkElement,
        href: href
      };
    }

    // Take screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_footer_links_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_footer_links_verified.png'));

    return {
      success: true,
      action: "Verified all footer links are present and properly structured",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

