import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the footer links to load
    await page.waitForSelector('.footer-link');

    // Extract footer links
    const footerLinks = await page.$$eval('.footer-link', links => {
      return links.map(link => ({
        text: link.innerText,
        href: link.href
      }));
    });

    // Check for expected links
    const expectedLinks = [
      { text: 'Home', href: 'index.html' },
      { text: 'My Events', href: 'dashboard.html' },
      { text: '+ New Event', href: 'create.html' },
      { text: 'Profile', href: '#' },
      { text: 'Settings', href: '#' },
      { text: 'Sign Out', href: '#' },
      { text: 'Create New Event', href: 'create.html' },
      { text: 'View All →', href: 'dashboard.html' }
    ];

    const missingLinks = expectedLinks.filter(expected => 
      !footerLinks.some(link => link.text === expected.text && link.href === expected.href)
    );

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: missingLinks.length === 0,
      action: missingLinks.length === 0 ? "All footer links are present and correctly structured." : "Some footer links are missing.",
      extractedData: {
        footerLinks,
        missingLinks
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
