import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for footer links to be present
    await page.waitForSelector('.footer-link', { timeout: 5000 });

    // Verify footer links
    const footerLinks = await page.$$eval('.footer-link', links => links.map(link => ({
      text: link.textContent.trim(),
      href: link.href
    })));

    // Expected footer links and their structure
    const expectedLinks = [
      { text: 'Home', href: 'index.html' },
      { text: 'My Events', href: 'dashboard.html' },
      { text: '+ New Event', href: 'create.html' },
      { text: 'Profile', href: '#' },
      { text: 'Settings', href: '#' },
      { text: 'Sign Out', href: '#' }
    ];

    const missingLinks = expectedLinks.filter(expectedLink => 
      !footerLinks.some(link => link.text === expectedLink.text && link.href === expectedLink.href)
    );

    if (missingLinks.length > 0) {
      throw new Error(`Missing footer links: ${missingLinks.map(link => link.text).join(', ')}`);
    }

    // Take screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_footer_links_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_footer_links_verified.png'));

    return {
      success: true,
      action: "Footer links verified successfully",
      extractedData: { footerLinks },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
