import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Gaming filter tag button to be visible
    await page.waitForSelector('button.tag', { timeout: 5000 });

    // Click on the Gaming filter tag button
    await page.click('button.tag:has-text("Gaming")');

    // Take screenshot after clicking the Gaming filter
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_gaming_filter_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_gaming_filter_clicked.png'));

    // Verify that the Gaming filter is active
    const isGamingActive = await page.$eval('button.tag.active', button => button.textContent.includes('Gaming'));

    return {
      success: true,
      action: "Clicked on the Gaming filter tag button to filter videos by gaming category.",
      extractedData: { isGamingActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

