import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Gaming filter tag button to be visible
    await page.waitForSelector('button.tag:has-text("Gaming")');

    // Click on the Gaming filter tag button
    await page.click('button.tag:has-text("Gaming")');

    // Wait for the content to load after filtering
    await page.waitForTimeout(1000); // Adjusting wait time for content to load

    // Take a screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_gaming_filter_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_gaming_filter_clicked.png'));

    // Extract meaningful data to verify task completion
    const filteredVideos = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.video-item')).map(video => ({
        title: video.querySelector('.video-title').innerText,
        url: video.querySelector('a').href
      }));
    });

    return {
      success: true,
      action: "Clicked on the Gaming filter tag button to filter videos by gaming category",
      extractedData: { filteredVideos },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
