import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Gaming filter tag button to be available
    await page.waitForSelector('button.tag:has-text("Gaming")');

    // Click on the Gaming filter tag button
    await page.click('button.tag:has-text("Gaming")');

    // Wait for the filtering action to complete (you can adjust the selector based on the expected loading)
    await page.waitForSelector('button.tag.active:has-text("Gaming")');

    // Take a screenshot after clicking the Gaming filter
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_gaming_filter_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_gaming_filter_clicked.png'));

    return {
      success: true,
      action: "Clicked on the 'Gaming' filter tag button to filter videos by gaming category.",
      extractedData: {
        currentActiveFilter: await page.evaluate(() => {
          return document.querySelector('button.tag.active').innerText;
        }),
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
