import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the "Gaming" filter tag button to be visible and clickable
    await page.waitForSelector('button.tag', { timeout: 5000 });

    // Click on the "Gaming" filter tag button
    const gamingButton = await page.$x("//button[contains(text(), 'Gaming')]");
    if (gamingButton.length > 0) {
      await gamingButton[0].click();
      await page.waitForTimeout(1000); // Wait for the filtering to take effect

      // Take a screenshot after clicking the button
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_gaming_clicked.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_gaming_clicked.png'));

      return {
        success: true,
        action: "Clicked on the 'Gaming' filter tag button",
        extractedData: {
          message: "Filtered videos by gaming category"
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error("Gaming button not found");
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
