import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input and type a query
    await page.waitForSelector('.search-input');
    await page.type('.search-input', 'jun morning routine');

    // Take screenshot after typing search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed_search.png'));

    // Click the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for search results header to appear (text: "Search results for")
    await page.waitForSelector('.search-header', { timeout: 5000 });

    // Take screenshot of search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Get the first search result's title and video id
    const firstResult = await page.waitForSelector('.search-result-item', { timeout: 5000 });
    const videoTitle = await firstResult.$eval('.result-title', el => el.textContent.trim());
    const videoId = await firstResult.getAttribute('data-video-id');

    // Click the first search result to go to the video player page
    await firstResult.click();

    // Wait for video player to load (wait for .video-player)
    await page.waitForSelector('.video-player', { timeout: 5000 });

    // Take screenshot of video player page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_video_player.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_video_player.png'));

    // Extract video title, channel name, and description
    const extractedData = {};
    extractedData.videoTitle = await page.$eval('.video-title', el => el.textContent.trim());
    extractedData.channelName = await page.$eval('.channel-name', el => el.textContent.replace(/\s+$/, ''));
    extractedData.description = await page.$eval('.video-description p', el => el.textContent.trim());

    // Scroll to comments section and wait for comments to appear
    await page.evaluate(() => {
      const el = document.querySelector('.comments-section');
      if (el) el.scrollIntoView();
    });
    await page.waitForSelector('.comments-list .comment', { timeout: 5000 });

    // Take screenshot of comments section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_comments.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_comments.png'));

    // Extract the first comment's username and text
    const firstComment = await page.$('.comments-list .comment');
    if (firstComment) {
      extractedData.firstCommentUser = await firstComment.$eval('.comment-author', el => el.textContent.trim());
      extractedData.firstCommentText = await firstComment.$eval('.comment-text', el => el.textContent.trim());
    }

    // Click the "Like" button for the video
    await page.waitForSelector('.like-btn');
    await page.click('.like-btn');
    // Wait a short moment for UI update
    await page.waitForTimeout(500);

    // Take screenshot after liking the video
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_liked_video.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_liked_video.png'));

    // Click the "Save" button (to open save to playlist modal)
    await page.waitForSelector('.save-btn');
    await page.click('.save-btn');
    await page.waitForSelector('.save-playlist-modal', { timeout: 5000 });

    // Take screenshot of save to playlist modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_7_save_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_7_save_modal.png'));

    // Check the first playlist checkbox (if not already checked)
    const firstPlaylistCheckbox = await page.$('.playlist-checkbox input[type="checkbox"]');
    if (firstPlaylistCheckbox) {
      const checked = await firstPlaylistCheckbox.isChecked();
      if (!checked) {
        await firstPlaylistCheckbox.click();
        await page.waitForTimeout(300);
      }
    }

    // Click the "Save" button in the modal
    const savePlaylistBtn = await page.$('.save-playlist-modal .btn-primary');
    if (savePlaylistBtn) {
      await savePlaylistBtn.click();
      // Wait for modal to disappear
      await page.waitForSelector('.save-playlist-modal', { state: 'detached', timeout: 5000 });
    }

    // Take screenshot after saving to playlist
    await page.screenshot({ path: path.join(screenshotsDir, 'step_8_saved_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_8_saved_playlist.png'));

    // Navigate to Library via nav-item (text: Library)
    const libraryNav = await page.$('a.nav-item:text("Library")');
    if (libraryNav) {
      await libraryNav.click();
    } else {
      // Fallback: click by selector
      const navItems = await page.$$('.nav-item');
      for (const nav of navItems) {
        const text = await nav.evaluate(el => el.textContent.trim());
        if (text === 'Library') {
          await nav.click();
          break;
        }
      }
    }
    // Wait for library page to load
    await page.waitForSelector('.page-header h1', { timeout: 5000 });
    // Take screenshot of library page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_9_library.png') });
    screenshots.push(path.join(screenshotsDir, 'step_9_library.png'));

    // Verify liked video appears in "Liked videos" section
    const likedVideosSection = await page.$('.library-section:has(h3:text("Liked videos"))');
    let likedVideoFound = false;
    if (likedVideosSection && videoTitle) {
      likedVideoFound = await likedVideosSection.$$eval('.video-title', (nodes, videoTitle) => {
        return nodes.some(n => n.textContent.trim() === videoTitle);
      }, videoTitle);
    }
    extractedData.likedVideoInLibrary = likedVideoFound;

    return {
      success: true,
      action: "Performed a search, viewed a video, liked and saved it to playlist, and verified it appears in Library",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
