import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the notification button to be visible and click it
    await page.waitForSelector('button.header-icon.notification-btn', { timeout: 5000 });
    await page.click('button.header-icon.notification-btn');

    // Take a screenshot after clicking the notification button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_notification_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_notification_clicked.png'));

    // Extracting data to verify task completion
    const notificationContent = await page.evaluate(() => {
      const notifications = document.querySelectorAll('.notification-toast');
      return Array.from(notifications).map(notification => notification.innerText);
    });

    return {
      success: true,
      action: "Clicked on the notification bell icon to view notifications.",
      extractedData: { notifications: notificationContent },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

