import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the notification bell button to be visible
    await page.waitForSelector('button.header-icon.notification-btn');

    // Click on the notification bell button
    await page.click('button.header-icon.notification-btn');

    // Take a screenshot after clicking the notification bell
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_notification_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_notification_clicked.png'));

    // Extract meaningful data to verify task completion
    const notificationVisible = await page.isVisible('.notification-toast');

    return {
      success: true,
      action: "Clicked on the notification bell icon",
      extractedData: { notificationVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

