import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the notification button to be visible and click it
    await page.waitForSelector('button.header-icon.notification-btn', { timeout: 5000 });
    await page.click('button.header-icon.notification-btn');

    // Take a screenshot after clicking the notification button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_notification_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_notification_clicked.png'));

    // Optional: Wait for notifications to load and check for some text or element
    await page.waitForSelector('.notification-toast', { timeout: 5000 });
    const notificationText = await page.evaluate(() => {
      const notification = document.querySelector('.notification-toast');
      return notification ? notification.innerText : null;
    });

    return {
      success: true,
      action: "Clicked on the notification bell icon and viewed notifications",
      extractedData: { notificationText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
