import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on each filter tag button with a 1 second delay
    const filterTags = await page.$$('.filter-tags .tag');
    for (const tag of filterTags) {
      await tag.click();
      await page.waitForTimeout(1000); // Wait for 1 second
      // Take a screenshot after each click
      await page.screenshot({ path: path.join(screenshotsDir, `step_${screenshots.length + 2}_after_click_${await tag.innerText()}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${screenshots.length + 2}_after_click_${await tag.innerText()}.png`));
    }

    return {
      success: true,
      action: "Clicked on each filter tag button with 1 second intervals",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

