import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter tags to be visible
    await page.waitForSelector('.filter-tags');

    // Get all filter tag buttons
    const filterTags = await page.$$('.filter-tags .tag');

    // Click each filter tag button one by one
    for (const tag of filterTags) {
      await tag.click();
      await page.waitForTimeout(1000); // Wait for 1 second between clicks

      // Take a screenshot after each click
      await page.screenshot({ path: path.join(screenshotsDir, `step_${filterTags.indexOf(tag) + 2}_clicked_${await tag.evaluate(el => el.innerText)}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${filterTags.indexOf(tag) + 2}_clicked_${await tag.evaluate(el => el.innerText)}.png`));
    }

    return {
      success: true,
      action: "Clicked on each filter tag button with 1 second delay between clicks",
      extractedData: { clickedTags: await Promise.all(filterTags.map(tag => tag.evaluate(el => el.innerText))) },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
