import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter tags to load
    await page.waitForSelector('.filter-tags');

    // Get all filter tag buttons
    const filterTags = await page.$$('.filter-tags .tag');

    // Loop through each tag and click
    for (const tag of filterTags) {
      await tag.click();
      // Take screenshot after each click
      await page.screenshot({ path: path.join(screenshotsDir, `step_tag_clicked_${await tag.evaluate(el => el.textContent.trim())}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_tag_clicked_${await tag.evaluate(el => el.textContent.trim())}.png`));
      // Wait for 1 second before next click
      await page.waitForTimeout(1000);
    }

    return {
      success: true,
      action: "Clicked on each filter tag with a delay",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
