import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find all filter tag buttons and click each one with a 1-second delay
    const filterTags = await page.$$('.filter-tags .tag');
    for (let tag of filterTags) {
      await tag.click();
      // Take a screenshot after each click
      await page.screenshot({ path: path.join(screenshotsDir, `step_${filterTags.indexOf(tag) + 2}_clicked_${await tag.innerText()}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${filterTags.indexOf(tag) + 2}_clicked_${await tag.innerText()}.png`));
      await page.waitForTimeout(1000); // Wait for 1 second
    }

    return {
      success: true,
      action: "Clicked on each filter tag button with a delay",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
