import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on each filter tag button with a 1 second delay
    const filterTags = await page.$$('.filter-tags .tag');
    for (let i = 0; i < filterTags.length; i++) {
      await filterTags[i].click();
      await page.waitForTimeout(1000); // Wait for 1 second
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_filter_tag_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_filter_tag_${i + 1}.png`));
    }

    return {
      success: true,
      action: "Clicked on each filter tag button with 1 second intervals",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
