      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked on each filter tag button with 1 second intervals.",
      extractedData: { clickedTags: tagsCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter tags to be visible
    await page.waitForSelector('.filter-tags');

    // Select filter buttons
    const filterButtons = await page.$$('.filter-tags .tag');

    // Loop through each button and click it
    for (const button of filterButtons) {
      await button.click();
      await page.waitForTimeout(1000); // 1 second delay
      // Take a screenshot after each click
      await page.screenshot({ path: path.join(screenshotsDir, `step_${screenshots.length + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${screenshots.length + 1}.png`));
    }

    return {
      success: true,
      action: "Clicked on all filter tags with a 1 second delay between clicks",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
