import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find all filter tag buttons
    const filterButtons = await page.$$('button.tag');

    // Click on each filter tag button with a delay
    for (const button of filterButtons) {
      await button.click();
      await page.waitForTimeout(1000); // Wait for 1 second between clicks
      // Take a screenshot after each click
      await page.screenshot({ path: path.join(screenshotsDir, `step_${filterButtons.indexOf(button) + 2}_clicked_${await button.evaluate(el => el.textContent.trim())}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${filterButtons.indexOf(button) + 2}_clicked_${await button.evaluate(el => el.textContent.trim())}.png`));
    }

    return {
      success: true,
      action: "Clicked on each filter tag button one by one with 1 second delay",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
