import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible and click on it
    await page.waitForSelector('.user-avatar', { state: 'visible' });
    await page.click('.user-avatar');

    // Take a screenshot after clicking the user avatar
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_menu_opened.png'));

    // Extract meaningful data to verify task completion
    const userMenuVisible = await page.isVisible('.user-menu'); // Assuming there's a user menu that appears
    const extractedData = { userMenuVisible };

    return {
      success: true,
      action: "Clicked on the user avatar to open the user menu",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

