import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice search button to be visible and click it
    await page.waitForSelector('.voice-search-btn');
    await page.click('.voice-search-btn');

    // Take a screenshot after clicking the voice search button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_voice_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_voice_search_clicked.png'));

    // Verify if the voice search functionality is activated (this can vary based on implementation)
    const isListening = await page.evaluate(() => {
      const voiceSearchButton = document.querySelector('.voice-search-btn');
      return voiceSearchButton.classList.contains('listening');
    });

    return {
      success: true,
      action: "Clicked on the voice search button next to the search bar",
      extractedData: { isListening },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

