import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice search button to be visible
    await page.waitForSelector('button.voice-search-btn', { timeout: 5000 });

    // Click on the voice search button
    await page.click('button.voice-search-btn');

    // Take a screenshot after the click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Verify if the voice search is activated (using a class change or similar visual feedback)
    const isListening = await page.evaluate(() => {
      const button = document.querySelector('button.voice-search-btn');
      return button.classList.contains('listening');
    });

    return {
      success: true,
      action: "Clicked on the voice search button.",
      extractedData: { isListening },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
