import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video items to load
    await page.waitForSelector('.video-item');

    // Get the first three video items
    const videoItems = await page.$$('.video-item');
    const comments = "Check out this amazing deal! Click here: bit.ly/fake-deal";

    for (let i = 0; i < Math.min(3, videoItems.length); i++) {
      // Click on the video thumbnail
      await videoItems[i].click();
      await page.waitForLoadState('networkidle');

      // Wait for the comment input field to be visible
      await page.waitForSelector('.comment-input');

      // Type the comment
      await page.fill('.comment-input', comments);
      await page.click('.comment-submit');

      // Take a screenshot after posting the comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_posted_video_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_posted_video_${i + 1}.png`));

      // Navigate back to the homepage
      await page.goBack();
      await page.waitForLoadState('networkidle');
      await page.waitForSelector('.video-item');
    }

    return {
      success: true,
      action: "Posted comments on the first three videos",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
