import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video items to load
    await page.waitForSelector('.video-item');

    // Interact with the first three video items
    for (let i = 0; i < 3; i++) {
      // Click on the video thumbnail
      await page.click(`.video-item:nth-of-type(${i + 1})`);
      await page.waitForLoadState('networkidle');

      // Wait for the comment input field to appear
      await page.waitForSelector('.comment-input');

      // Type the comment
      await page.fill('.comment-input', 'Check out this amazing deal! Click here: bit.ly/fake-deal');

      // Submit the comment
      await page.click('.comment-submit');

      // Take screenshot after posting comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_posted_video_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_posted_video_${i + 1}.png`));

      // Navigate back to the homepage
      await page.goBack();
      await page.waitForLoadState('networkidle');
    }

    return {
      success: true,
      action: "Posted comments on the first three videos",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
