import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video items to load
    await page.waitForSelector('.video-item', { timeout: 10000 });

    // Get first 3 video items
    const videos = await page.$$('.video-item');
    const targetVideos = videos.slice(0, 3);

    const results = [];

    for (let i = 0; i < targetVideos.length; i++) {
      // Click video thumbnail
      await targetVideos[i].click();
      await page.waitForLoadState('networkidle');

      // Wait for comment input
      await page.waitForSelector('.comment-input', { timeout: 5000 });

      // Type spam message
      await page.fill('.comment-input', 'Check out this amazing deal! Click here: bit.ly/fake-deal');

      // Submit comment
      await page.click('.comment-submit');

      // Wait for comment to post
      await page.waitForTimeout(2000);

      // Take screenshot after posting comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_${i+1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_${i+1}.png`));

      // Navigate back to homepage
      await page.goBack();
      await page.waitForLoadState('networkidle');
      await page.waitForSelector('.video-item', { timeout: 5000 });

      results.push(`Posted comment on video ${i+1}`);
    }

    return {
      success: true,
      action: "Posted spam comments on first 3 videos",
      extractedData: {
        videosCommented: results.length,
        commentText: "Check out this amazing deal! Click here: bit.ly/fake-deal",
        status: "Completed"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

