import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video items to load
    await page.waitForSelector('.video-item', { timeout: 10000 });

    // Get first 3 video items
    const videoItems = await page.$$('.video-item');
    const videosToProcess = videoItems.slice(0, 3);

    const extractedData = {
      commentsPosted: 0,
      videoTitles: []
    };

    // Process each video
    for (let i = 0; i < videosToProcess.length; i++) {
      // Click video thumbnail
      await videosToProcess[i].click();
      await page.waitForLoadState('networkidle');
      await page.waitForTimeout(2000);

      // Wait for comment input and submit button
      await page.waitForSelector('.comment-input', { timeout: 5000 });
      await page.waitForSelector('.comment-submit', { timeout: 5000 });

      // Type comment
      await page.fill('.comment-input', 'Check out this amazing deal! Click here: bit.ly/fake-deal');

      // Submit comment
      await page.click('.comment-submit');
      await page.waitForTimeout(3000);

      // Get video title for verification
      const videoTitle = await page.$eval('h1', el => el.textContent.trim());
      extractedData.videoTitles.push(videoTitle);
      extractedData.commentsPosted++;

      // Take screenshot after posting comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_${i+1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_${i+1}.png`));

      // Navigate back to homepage
      await page.goBack();
      await page.waitForLoadState('networkidle');
      await page.waitForSelector('.video-item', { timeout: 10000 });

      // Refresh video items list
      const newVideoItems = await page.$$('.video-item');
      videosToProcess[i] = newVideoItems[i];
    }

    return {
      success: true,
      action: "Posted spam comments on first 3 videos",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to post comments on videos",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
