import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video titles and channel names to load
    await page.waitForSelector('.video-title');
    await page.waitForSelector('.channel-name');

    // Extract video titles and channel names
    const videoTitles = await page.$$eval('.video-title', titles => titles.map(title => title.textContent.trim()));
    const channelNames = await page.$$eval('.channel-name', channels => channels.map(channel => channel.textContent.trim()));

    // Structure the extracted data
    const extractedData = videoTitles.map((title, index) => ({
      title,
      channel: channelNames[index] || 'Unknown Channel'
    }));

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extraction.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video title and channel name elements to load
    await page.waitForSelector('.video-title');
    await page.waitForSelector('.channel-name');

    // Extract video titles and channel names
    const videoData = await page.evaluate(() => {
      const videoElements = document.querySelectorAll('.video-title');
      const channelElements = document.querySelectorAll('.channel-name');
      const data = [];

      videoElements.forEach((video, index) => {
        const title = video.innerText;
        const channel = channelElements[index] ? channelElements[index].innerText : 'Unknown Channel';
        data.push({ title, channel });
      });

      return data;
    });

    // Take a screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

