import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video title and channel name elements to be visible
    await page.waitForSelector('.video-title', { visible: true });
    await page.waitForSelector('.channel-name', { visible: true });

    // Extract video titles and channel names
    const videoData = await page.evaluate(() => {
      const titles = Array.from(document.querySelectorAll('.video-title')).map(title => title.innerText);
      const channels = Array.from(document.querySelectorAll('.channel-name')).map(channel => channel.innerText);

      return titles.map((title, index) => ({
        title,
        channel: channels[index] || 'Unknown Channel'
      }));
    });

    // Log extracted data
    console.log(videoData);

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
