    // Wait for video titles and channel names to load
    await page.waitForSelector('.video-title, .channel-name');

    // Extract video titles and channel names
    const videos = await page.evaluate(() => {
      const videoElements = document.querySelectorAll('.video-title');
      const channelElements = document.querySelectorAll('.channel-name');

      const videoData = [];
      videoElements.forEach((video, index) => {
        videoData.push({
          title: video.textContent.trim(),
          channel: channelElements[index] ? channelElements[index].textContent.trim() : 'Unknown Channel'
        });
      });
      return videoData;
    });

    // Log extracted data
    console.log(videos);

    return {
      success: true,
      action: "Extracted video titles and channel names from homepage",
      extractedData: videos,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

