import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video titles and channel names to load
    await page.waitForSelector('.video-title', { timeout: 5000 });
    await page.waitForSelector('.channel-name', { timeout: 5000 });

    // Extract video titles and channel names
    const videoData = await page.evaluate(() => {
      const videos = document.querySelectorAll('.video-title');
      const channels = document.querySelectorAll('.channel-name');
      const data = [];

      videos.forEach((video, index) => {
        if (channels[index]) {
          data.push({
            title: video.textContent.trim(),
            channel: channels[index].textContent.trim()
          });
        }
      });

      return data;
    });

    // Take screenshot of the extracted data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_data.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
