import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video titles and channel names to load
    await page.waitForSelector('.video-title');
    await page.waitForSelector('.channel-name');

    // Extract video titles and channel names
    const videoData = await page.evaluate(() => {
      const videos = Array.from(document.querySelectorAll('.video-title'));
      const channels = Array.from(document.querySelectorAll('.channel-name'));
      return videos.map((video, index) => ({
        title: video.innerText,
        channel: channels[index] ? channels[index].innerText : null
      }));
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_data.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

