import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video elements to load
    await page.waitForSelector('.video-title', { timeout: 5000 });
    await page.waitForSelector('.channel-name', { timeout: 5000 });

    // Extract video titles and channel names
    const videoElements = await page.$$('.video-title');
    const channelElements = await page.$$('.channel-name');

    const extractedData = [];

    for (let i = 0; i < videoElements.length; i++) {
      const title = await videoElements[i].innerText();
      const channel = await channelElements[i].innerText();
      extractedData.push({ title, channel });
    }

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extraction.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
