import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video elements to load
    await page.waitForSelector('.video-title, .channel-name', { timeout: 10000 });

    // Extract video titles and channel names
    const videos = await page.evaluate(() => {
      const videoElements = Array.from(document.querySelectorAll('.video-title'));
      const channelElements = Array.from(document.querySelectorAll('.channel-name'));

      return videoElements.map((video, index) => ({
        title: video.innerText,
        channel: channelElements[index]?.innerText || 'Unknown Channel'
      }));
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extraction.png'));

    return {
      success: true,
      action: "Extracted video titles and channel names from the homepage",
      extractedData: videos,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
