import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page
    await page.waitForSelector('.nav-item[data-page="library"]');
    await page.click('.nav-item[data-page="library"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Click the first available playlist
    await page.waitForSelector('.playlist-card:first-child');
    await page.click('.playlist-card:first-child');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_first_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_first_playlist.png'));

    // Extract video information from the playlist
    const videoItems = await page.$$eval('.playlist-video-item', items => {
      return items.map(item => ({
        title: item.querySelector('.video-title').innerText,
        url: item.querySelector('a').href,
        duration: item.querySelector('.video-duration').innerText
      }));
    });

    // Create a new playlist with modified title
    await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await page.click('.btn-primary[onclick*="createPlaylist"]');
    await page.waitForSelector('#playlist-name');
    await page.type('#playlist-name', `New Playlist - ${Date.now()}`);
    await page.click('.modal-close'); // Assuming there's a close button to save the playlist
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_new_playlist_created.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_new_playlist_created.png'));

    // Add all videos to the new playlist
    for (const video of videoItems) {
      await page.goto(video.url);
      await page.waitForSelector('.add-to-playlist-button'); // Adjust selector as necessary
      await page.click('.add-to-playlist-button');
      await page.waitForSelector('.playlist-selection'); // Adjust selector as necessary
      await page.select('.playlist-selection', 'new_playlist_id'); // Replace with the actual ID of the new playlist
      await page.waitForLoadState('networkidle');
    }

    return {
      success: true,
      action: "Extracted video information and created a new playlist with the same videos",
      extractedData: { videoItems },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to the Library page
    await page.waitForSelector('.nav-item[data-page="library"]');
    await page.click('.nav-item[data-page="library"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Click on the first available playlist
    await page.waitForSelector('.playlist-card:first-child');
    await page.click('.playlist-card:first-child');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_first_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_first_playlist.png'));

    // Extract video information
    const videoItems = await page.$$('.playlist-video-item');
    const videoData = [];
    for (const item of videoItems) {
      const title = await item.$eval('.video-title', el => el.textContent.trim());
      const url = await item.$eval('a', el => el.href);
      videoData.push({ title, url });
    }

    // Create a new playlist with modified title
    await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await page.click('.btn-primary[onclick*="createPlaylist"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_create_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_create_playlist.png'));

    // Fill in the new playlist details and save
    const newPlaylistName = `Copied Playlist - ${Date.now()}`;
    await page.fill('input#playlist-name', newPlaylistName);
    await page.fill('textarea#playlist-description', 'This is a copied playlist with modified title.');
    await page.click('button.save-playlist'); // Assumed selector for save button
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_playlist_created.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_playlist_created.png'));

    // Add videos to new playlist
    for (const video of videoData) {
      await page.goto(video.url);
      await page.waitForSelector('.add-to-playlist-button'); // Assumed selector
      await page.click('.add-to-playlist-button');
      await page.select('select.playlist-dropdown', newPlaylistName); // Assumed selector
      await page.click('button.add-video-to-playlist'); // Assumed selector
      await page.waitForTimeout(1000); // Wait for the video to be added
    }

    return {
      success: true,
      action: "Successfully created a new playlist and added videos",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

