import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page
    await page.waitForSelector('.nav-item[data-page="library"]');
    await page.click('.nav-item[data-page="library"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot of Library page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Click on the first available playlist
    await page.waitForSelector('.playlist-card:first-child');
    await page.click('.playlist-card:first-child');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the first playlist
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_first_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_first_playlist.png'));

    // Extract video information from the playlist
    const videoElements = await page.$$('.playlist-video-item');
    const videos = [];
    for (const videoElement of videoElements) {
      const title = await videoElement.$eval('.video-title', el => el.textContent.trim());
      const url = await videoElement.$eval('a', el => el.href);
      videos.push({ title, url });
    }

    // Create a new playlist with modified title
    await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await page.click('.btn-primary[onclick*="createPlaylist"]');
    await page.waitForSelector('#playlist-name');
    await page.fill('#playlist-name', `New Playlist - ${Date.now()}`);
    await page.click('.modal-content .btn-primary'); // Assuming there's a primary button to create the playlist
    await page.waitForLoadState('networkidle');

    // Add videos to the new playlist
    for (const video of videos) {
      await page.goto(video.url);
      await page.waitForSelector('.btn-add-to-playlist'); // Assuming there's a button to add to playlist
      await page.click('.btn-add-to-playlist');
      await page.waitForSelector('.playlist-select'); // Assuming there's a dropdown to select playlist
      await page.select('.playlist-select', 'new_playlist_id'); // Replace with actual new playlist ID
      await page.click('.btn-confirm-add'); // Assuming there's a button to confirm adding
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_add_videos.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_add_videos.png'));

    return {
      success: true,
      action: "Successfully created a new playlist and added videos",
      extractedData: { videos },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page
    await page.click('a.nav-item[data-page="library"]');
    await page.waitForSelector('.playlist-card:first-child');

    // Take screenshot of the Library page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Click on the first available playlist
    await page.click('.playlist-card:first-child');
    await page.waitForSelector('.playlist-video-item');

    // Extract video information
    const videos = await page.$$eval('.playlist-video-item', items => {
      return items.map(item => ({
        title: item.querySelector('.video-title').innerText,
        duration: item.querySelector('.video-duration').innerText,
        url: item.querySelector('a').href
      }));
    });

    // Take screenshot of the playlist videos
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_playlist_videos.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_playlist_videos.png'));

    // Create a new playlist
    await page.click('button.btn-primary[onclick*="createPlaylist"]');
    await page.waitForSelector('#playlist-name');

    // Modify the title and save the playlist
    const newPlaylistTitle = `Copy of ${videos[0].title}`;
    await page.fill('#playlist-name', newPlaylistTitle);
    await page.fill('#playlist-description', 'This is a copied playlist');
    await page.click('button.save-playlist');

    // Take a screenshot of the new playlist creation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_create_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_create_playlist.png'));

    // Add all videos to the new playlist
    for (const video of videos) {
      await page.goto(video.url);
      await page.waitForSelector('.add-to-playlist-button');
      await page.click('.add-to-playlist-button');
      await page.selectOption('select.playlist-selector', newPlaylistTitle);
    }

    // Final screenshot after adding videos
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_add_videos_to_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_add_videos_to_playlist.png'));

    return {
      success: true,
      action: "Navigated to Library, extracted video information, created a new playlist, and added videos.",
      extractedData: { videos, newPlaylistTitle },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

