import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page
    await page.waitForSelector('.nav-item[data-page="library"]');
    await page.click('.nav-item[data-page="library"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot of Library page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Click on the first available playlist
    await page.waitForSelector('.playlist-card:first-child');
    await page.click('.playlist-card:first-child');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the playlist page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_playlist_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_playlist_page.png'));

    // Extract video information from the playlist
    const videoItems = await page.$$eval('.playlist-video-item', items => {
      return items.map(item => ({
        title: item.querySelector('.video-title').innerText,
        duration: item.querySelector('.video-duration').innerText,
        link: item.querySelector('a').href
      }));
    });

    // Create a new playlist with modified title
    await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await page.click('.btn-primary[onclick*="createPlaylist"]');
    await page.waitForSelector('#playlist-name');

    // Modify the title for the new playlist
    const newPlaylistTitle = `Copy of ${videoItems[0].title}`;
    await page.fill('#playlist-name', newPlaylistTitle);
    await page.click('.modal-content .btn-primary'); // Assuming there's a button to confirm creation

    // Wait for the new playlist to be created
    await page.waitForLoadState('networkidle');

    // Add videos to the new playlist
    for (const video of videoItems) {
      await page.goto(video.link);
      await page.waitForSelector('.btn-add-to-playlist');
      await page.click('.btn-add-to-playlist');
      await page.waitForSelector('.playlist-selector');
      await page.selectOption('.playlist-selector', newPlaylistTitle);
      await page.click('.btn-confirm-add'); // Assuming there's a button to confirm adding
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: true,
      action: "Successfully created a new playlist and added videos to it.",
      extractedData: { videoItems, newPlaylistTitle },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

