import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page
    await page.waitForSelector('a.nav-item[data-page="library"]');
    await page.click('a.nav-item[data-page="library"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Click first available playlist
    await page.waitForSelector('.playlist-card:first-child');
    await page.click('.playlist-card:first-child');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_playlist_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_playlist_opened.png'));

    // Extract video information from playlist
    const videos = await page.$$eval('.playlist-video-item', items => 
      items.map(item => ({
        title: item.querySelector('.video-title')?.innerText || 'Unknown',
        duration: item.querySelector('.video-duration')?.innerText || 'Unknown',
        channel: item.querySelector('.channel-name')?.innerText || 'Unknown'
      }))
    );

    // Create new playlist
    await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await page.click('.btn-primary[onclick*="createPlaylist"]');
    await page.waitForSelector('#playlist-name');

    // Fill playlist name with modified title
    const originalPlaylistName = await page.$eval('.playlist-title', el => el.textContent);
    const modifiedName = `${originalPlaylistName} - COPY`;
    await page.fill('#playlist-name', modifiedName);

    // Create playlist
    await page.click('.modal-content .btn-primary');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_new_playlist_created.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_new_playlist_created.png'));

    return {
      success: true,
      action: "Navigated to Library, extracted videos from first playlist, created new playlist with modified name",
      extractedData: {
        originalPlaylistName,
        modifiedPlaylistName: modifiedName,
        videos: videos,
        videoCount: videos.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete playlist copy task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page
    const libraryLink = await page.waitForSelector('a[data-page="library"]');
    await libraryLink.click();
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Find and click the first playlist
    const firstPlaylist = await page.waitForSelector('.playlist-card:first-child');
    await firstPlaylist.click();
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_playlist_detail.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_playlist_detail.png'));

    // Extract all video information
    const videos = await page.$$eval('.playlist-video-item', items => 
      items.map(item => ({
        title: item.querySelector('.video-title')?.textContent?.trim() || 'Unknown',
        duration: item.querySelector('.video-duration')?.textContent?.trim() || 'Unknown',
        channel: item.querySelector('.channel-name')?.textContent?.trim() || 'Unknown'
      }))
    );

    // Create new playlist
    const createButton = await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await createButton.click();
    await page.waitForSelector('#playlist-name');

    // Fill playlist name (original name + " - Copy")
    const originalPlaylistName = await page.textContent('.playlist-title');
    await page.fill('#playlist-name', `${originalPlaylistName} - Copy`);
    await page.click('#privacy-public');
    await page.click('.modal-content .btn-primary');

    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_new_playlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_new_playlist.png'));

    // Add all videos to new playlist
    for (const video of videos) {
      // Find and click add to playlist button for each video
      const addButtons = await page.$$('.add-to-playlist-btn');
      for (const button of addButtons) {
        await button.click();
        await page.waitForTimeout(500);
      }
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_final.png'));

    return {
      success: true,
      action: "Successfully navigated to Library, extracted videos from first playlist, created new playlist with modified name, and added all videos",
      extractedData: {
        originalPlaylistName,
        videosCount: videos.length,
        videos: videos
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete playlist duplication task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Library page using data-page attribute selector
    await page.waitForSelector('[data-page="library"]');
    await page.click('[data-page="library"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_library_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_library_page.png'));

    // Find and click the first available playlist
    await page.waitForSelector('.playlist-card:first-child');
    const firstPlaylist = await page.$('.playlist-card:first-child');
    await firstPlaylist.click();
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_playlist_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_playlist_opened.png'));

    // Extract all video information from the playlist
    await page.waitForSelector('.playlist-video-item');
    const videoInfo = await page.$$eval('.playlist-video-item', items => 
      items.map(item => ({
        title: item.querySelector('.video-title')?.textContent.trim() || 'Unknown',
        duration: item.querySelector('.video-duration')?.textContent.trim() || 'Unknown',
        channel: item.querySelector('.channel-name')?.textContent.trim() || 'Unknown'
      }))
    );

    // Go back to library page
    await page.goBack();
    await page.waitForLoadState('networkidle');

    // Create new playlist using button with onclick containing "createPlaylist"
    await page.waitForSelector('.btn-primary[onclick*="createPlaylist"]');
    await page.click('.btn-primary[onclick*="createPlaylist"]');

    // Wait for playlist creation modal and fill form
    await page.waitForSelector('#playlist-name');
    await page.fill('#playlist-name', 'Copy of My Playlist');
    await page.click('button:has-text("Create")');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_playlist_created.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_playlist_created.png'));

    return {
      success: true,
      action: "Successfully navigated to Library, extracted videos from first playlist, and created a new playlist copy",
      extractedData: {
        originalVideos: videoInfo,
        newPlaylistName: 'Copy of My Playlist',
        videosCount: videoInfo.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
