import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video element to load
    await page.waitForSelector('video');

    // Click on the first video
    await page.click('video');

    // Wait for comments section to load
    await page.waitForSelector('.comment-text');

    // Extract comments containing the word "tutorial"
    const comments = await page.$$eval('.comment-text', elements => 
      elements.filter(el => el.textContent.includes('tutorial')).map(el => el.textContent)
    );

    // Loop through each comment and reply
    for (const comment of comments) {
      // Click the reply button for the comment
      await page.click(`xpath=//div[contains(text(), "${comment}")]/following-sibling::button[contains(@class, "reply-btn")]`);

      // Wait for the comment input to appear
      await page.waitForSelector('.comment-input');

      // Type the promotional message
      await page.fill('.comment-input', 'Download better tutorials at fake-site.com');

      // Submit the reply
      await page.click('.comment-submit');

      // Take a screenshot after each reply
      await page.screenshot({ path: path.join(screenshotsDir, `step_reply_to_${comment}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_reply_to_${comment}.png`));
    }

    return {
      success: true,
      action: "Replied to comments containing 'tutorial'",
      extractedData: { comments },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
