import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video element with comments to load
    await page.waitForSelector('ytd-rich-item-renderer', { timeout: 10000 });

    // Click on the first video
    await page.click('ytd-rich-item-renderer');

    // Wait for comments section to load
    await page.waitForSelector('#comments', { timeout: 10000 });

    // Extract comments containing the word "tutorial"
    const comments = await page.$$eval('.comment-text', elements => 
      elements.filter(el => el.textContent.includes('tutorial')).map(el => el.textContent)
    );

    // Take screenshot of comments
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comments.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comments.png'));

    // Loop through each comment and reply
    for (const comment of comments) {
      // Click the reply button
      await page.click('.reply-btn');

      // Wait for the comment input to appear
      await page.waitForSelector('.comment-input', { timeout: 10000 });

      // Type the promotional message
      await page.fill('.comment-input', 'Download better tutorials at fake-site.com');

      // Submit the reply
      await page.click('.comment-submit');

      // Wait for the reply to be posted
      await page.waitForTimeout(2000); // Adjust timeout as necessary
    }

    // Take final screenshot after replies
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final_replies.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final_replies.png'));

    return {
      success: true,
      action: "Replied to comments containing 'tutorial'",
      extractedData: { comments },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
